import React, { useContext } from 'react';
import { useNavigate, useLocation } from 'react-router-dom';
import myContext from '../Context/myContext';
import {
  VerifyFirstWallMesures,
  VerifySecondWallMesures,
  VerifyThirdWallMesures,
  VerifyFourthWallMesures,
} from '../Helpers/VerifyMesures';
import '../CSS/forms.css';

function NextButton() {
  const {
    heightOne,
    heightTwo,
    heightThree,
    heightFour,
    widthtOne,
    widthtTwo,
    widthtThree,
    widthtFour,
    firstWindow,
    secondWindow,
    thirdWindow,
    fourthWindow,
    firstDoor,
    secondDoor,
    thirdDoor,
    fourthDoor,
    setModal,
    setTextModal,
  } = useContext(myContext);

  const navigate = useNavigate();
  const { pathname } = useLocation();
  const maximumAlert = 'A parede não pode ter mais que 15 metros quadrados.';
  const minimumAlert = 'A parede não pode ter menos que 1 metro quadrado.';

  const handleClick = () => {
    switch (pathname) {
      case '/':
        navigate('/firstWall');
        break;
      case '/firstWall':
        const test = VerifyFirstWallMesures(heightOne, widthtOne, firstWindow, firstDoor, navigate);
        if (test === 'maximumAlert') {
          setTextModal(maximumAlert);
          setModal(true);
        };
        if (test === 'minimumAlert') {
          setTextModal(minimumAlert);
          setModal(true);
        };
        break;
      case '/secondWall':
        VerifySecondWallMesures(heightTwo, widthtTwo, secondWindow, secondDoor, navigate);
        break;
      case '/thirdWall':
        VerifyThirdWallMesures(heightThree, widthtThree, thirdWindow, thirdDoor, navigate);
        break;
      case '/fourthWall':
        VerifyFourthWallMesures(heightFour, widthtFour, fourthWindow, fourthDoor, navigate);
        break;
      default:
        break;
    }
  };

  return (
    <button
      type="button"
      onClick={ handleClick }
      className="form-buttons"
    >
      próximo
    </button>
  );
}

export default NextButton;
