import React, { useContext } from 'react';
import { useNavigate, useLocation } from 'react-router-dom';
import myContext from '../Context/myContext';
import '../CSS/forms.css';

function NextButton() {
  const {
    heightOne,
    // heightTwo,
    // heightThree,
    // heightFour,
    widthtOne,
    // widthtTwo,
    // widthtThree,
    // widthtFour,
    // firstWindow,
    // secondtWindow,
    // thirdtWindow,
    // fourthWindow,
    // firstDoor,
    // secondtDoor,
    // thirdtDoor,
    // fourthDoor,
  } = useContext(myContext);

  const navigate = useNavigate();
  const { pathname } = useLocation();

  const handleClick = () => {
    const magicNuber = 150000;
    switch (pathname) {
      case '/':
        navigate('/firstWall')
        break;
      case '/firstWall':
        if (
          Math.ceil(Number(heightOne) * Number(widthtOne)) > magicNuber
        ) {
          return alert('A parede não pode ter mais que 15 metros quadrados.')
        }
        navigate('/secondWall')
        break;
      case '/secondWall':
        navigate('/thirdWall')
        break;
      case '/thirdWall':
        navigate('/fourthWall')
        break;
      case '/fourthWall':
        navigate('/result')
        break;
      default:
        break;
    }
  };

  return (
    <button
      type="button"
      onClick={ handleClick }
      className="form-buttons"
    >
      próximo
    </button>
  );
}

export default NextButton;
