import React, { useContext } from 'react';
import { useNavigate, useLocation } from 'react-router-dom';
import myContext from '../Context/myContext';
import '../CSS/forms.css';

function NextButton() {
  const {
    heightOne,
    heightTwo,
    heightThree,
    heightFour,
    widthtOne,
    widthtTwo,
    widthtThree,
    widthtFour,
    // firstWindow,
    // secondWindow,
    // thirdWindow,
    // fourthWindow,
    // firstDoor,
    // secondDoor,
    // thirdDoor,
    // fourthDoor,
  } = useContext(myContext);

  const navigate = useNavigate();
  const { pathname } = useLocation();
  const firstWall = Math.ceil(Number(heightOne) * Number(widthtOne));
  const secondWall = Math.ceil(Number(heightTwo) * Number(widthtTwo));
  const thirdWall = Math.ceil(Number(heightThree) * Number(widthtThree));
  const fourthWall = Math.ceil(Number(heightFour) * Number(widthtFour));

  // const windowSquare = 24000;
  // const wallWindowOne = Math.ceil(Number(firstWindow)) * windowSquare;
  // const wallWindowTwo = Math.ceil(Number(secondWindow)) * windowSquare;
  // const wallWindowThree = Math.ceil(Number(thirdWindow)) * windowSquare;
  // const wallWindowFour = Math.ceil(Number(fourthWindow)) * windowSquare;

  // const doorSquare = 15200;
  // const wallDoorOne = Math.ceil(Number(firstDoor)) * doorSquare;
  // const wallDoorTwo = Math.ceil(Number(secondDoor)) * doorSquare;
  // const wallDoorThree = Math.ceil(Number(thirdDoor)) * doorSquare;
  // const wallDoorFour = Math.ceil(Number(fourthDoor)) * doorSquare;

  const handleClick = () => {
    const maximum = 150000;
    const minimun = 10000;

    switch (pathname) {
      case '/':
        navigate('/firstWall')
        break;
      case '/firstWall':
        if (firstWall > maximum) {
          return alert('A parede não pode ter mais que 15 metros quadrados.')
        }
        if (firstWall < minimun) {
          return alert('A parede não pode ter menos que 1 metro quadrado.')
        }
        navigate('/secondWall')
        break;
      case '/secondWall':
        if (secondWall > maximum) {
          return alert('A parede não pode ter mais que 15 metros quadrados.')
        }
        if (secondWall < minimun) {
          return alert('A parede não pode ter menos que 1 metro quadrado.')
        }
        navigate('/thirdWall')
        break;
      case '/thirdWall':
        if (thirdWall > maximum) {
          return alert('A parede não pode ter mais que 15 metros quadrados.')
        }
        if (thirdWall < minimun) {
          return alert('A parede não pode ter menos que 1 metro quadrado.')
        }
        navigate('/fourthWall')
        break;
      case '/fourthWall':
        if (fourthWall > maximum) {
          return alert('A parede não pode ter mais que 15 metros quadrados.')
        }
        if (fourthWall < minimun) {
          return alert('A parede não pode ter menos que 1 metro quadrado.')
        }
        navigate('/result')
        break;
      default:
        break;
    }
  };

  return (
    <button
      type="button"
      onClick={ handleClick }
      className="form-buttons"
    >
      próximo
    </button>
  );
}

export default NextButton;
