import React from 'react';
import PropTypes from 'prop-types';
import myContext from '../Context/myContext';
import useContext from '../Context/myContext';
// import { Modal, Button } from 'react-bootstrap'
import '../CSS/inputs.css'

function HeightAndWidth({ handleFunctions }) {
  // const { modal, setModal } = useContext(myContext);
  const {
    handleHeight,
    handleWidth,
    height,
    width
  } = handleFunctions;

  return(
    <section className="inputs-container">
      <div className="unique-label">
        {/* <Modal show={ modal } onHide={ () => setModal(false) }>
          <Modal.Header closeButton>
            <Modal.Title>Modal heading</Modal.Title>
          </Modal.Header>
          <Modal.Body>Woohoo, you're reading this text in a modal!</Modal.Body>
          <Modal.Footer>
            <Button variant="secondary" onClick={ () => setModal(false) }>
              Close
            </Button>
          </Modal.Footer>
        </Modal> */}
        <label
          htmlFor="height"
        >
        ALTURA
        <input
          type="number"
          id="height"
          placeholder="centímetros"
          className="form-control"
          value={ height }
          onChange={ handleHeight }
          data-testid="height-input"
        />
        </label>
      </div>
      <div className="unique-label">
        <label
          htmlFor="width"
        >
          LARGURA
          <input
            type="number"
            id="width"
            placeholder="centímetros"
            className="form-control"
            value={ width }
            onChange={ handleWidth }
            data-testid="width-input"
          />
        </label>
      </div>
    </section>
  );
}

HeightAndWidth.propTypes = {
  handleFunctions: PropTypes.object.isRequired,
};

export default HeightAndWidth;
