import React, { useContext, useEffect } from 'react';
import myContext from '../Context/myContext';
import '../CSS/main.css';

function ResultPage() {
  const {
    name,
    heightOne,
    heightTwo,
    heightThree,
    heightFour,
    widthtOne,
    widthtTwo,
    widthtThree,
    widthtFour,
    firstWindow,
    secondWindow,
    thirdWindow,
    fourthWindow,
    firstDoor,
    secondDoor,
    thirdDoor,
    fourthDoor,
  } = useContext(myContext);
  const windowSquare = 24000;
  const doorSquare = 15200;

  useEffect(() => {
    const sumAllHeights = Number(heightOne) + Number(heightTwo) + Number(heightThree) + Number(heightFour);
    const sumAllWidths = Number(widthtOne) + Number(widthtTwo) + Number(widthtThree) + Number(widthtFour);
    const sumAllWindows = Number(firstWindow) + Number(secondWindow) + Number(thirdWindow) + Number(fourthWindow);
    const sumAllDoors = Number(firstDoor) + Number(secondDoor) + Number(thirdDoor) + Number(fourthDoor);
    const total = (sumAllHeights * sumAllWidths) - ((sumAllWindows * windowSquare) + (sumAllDoors * doorSquare));
    console.log(total);
  });

  return (
    <div className="page-container">
      <div className="results">
        <h1>Olá {name}.</h1>
        <h1>Você precisa de</h1>
      </div>
    </div>
  )
}

export default ResultPage;
