import React, { useContext } from 'react';
import { useNavigate, useLocation } from 'react-router-dom';
import myContext from '../Context/myContext';
import {
  verifyFirstWallMesures,
  verifySecondWallMesures,
  verifyThirdWallMesures,
  verifyFourthWallMesures,
} from '../Helpers/verifyMesures';
import '../CSS/forms.css';

function NextButton() {
  const {
    heightOne,
    heightTwo,
    heightThree,
    heightFour,
    widthtOne,
    widthtTwo,
    widthtThree,
    widthtFour,
  } = useContext(myContext);

  const navigate = useNavigate();
  const { pathname } = useLocation();

  const handleClick = () => {
    switch (pathname) {
      case '/':
        navigate('/firstWall');
        break;
      case '/firstWall':
        verifyFirstWallMesures(heightOne, widthtOne);
        navigate('/secondWall');
        break;
      case '/secondWall':
        verifySecondWallMesures(heightTwo, widthtTwo);
        navigate('/thirdWall');
        break;
      case '/thirdWall':
        verifyThirdWallMesures(heightThree, widthtThree);
        navigate('/fourthWall')
        break;
      case '/fourthWall':
        verifyFourthWallMesures(heightFour, widthtFour);
        navigate('/result')
        break;
      default:
        break;
    }
  };

  return (
    <button
      type="button"
      onClick={ handleClick }
      className="form-buttons"
