import React, { useContext, useEffect, useState } from 'react';
import myContext from '../Context/myContext';
import '../CSS/results.css';

function ResultPage() {
  const {
    name,
    heightOne,
    heightTwo,
    heightThree,
    heightFour,
    widthtOne,
    widthtTwo,
    widthtThree,
    widthtFour,
    firstWindow,
    secondWindow,
    thirdWindow,
    fourthWindow,
    firstDoor,
    secondDoor,
    thirdDoor,
    fourthDoor,
  } = useContext(myContext);
  const [totalSquare, setTotalSquare] = useState(0);
  const [allCans, setAllCans] = useState([]);

  const windowSquare = 2.4;
  const doorSquare = 1.52;
  const squareMeters = 10000;

  // useEffect para calcular quantos metros quadrados de parede existe para pintura
  useEffect(() => {
    const wallOne = (Number(heightOne) * Number(widthtOne)) / squareMeters;
    const wallTwo = (Number(heightTwo) * Number(widthtTwo)) / squareMeters;
    const wallThree = (Number(heightThree) * Number(widthtThree)) / squareMeters;
    const wallFour = (Number(heightFour) * Number(widthtFour)) / squareMeters;

    const sumAllWindows = (Number(firstWindow) + Number(secondWindow) + Number(thirdWindow) + Number(fourthWindow)) * windowSquare;
    const sumAllDoors = (Number(firstDoor) + Number(secondDoor) + Number(thirdDoor) + Number(fourthDoor)) * doorSquare;
    const total = Math.ceil((wallOne + wallTwo + wallThree + wallFour) - (sumAllWindows + sumAllDoors));

    setTotalSquare(total);
  }, [setTotalSquare]);

// useEffect para separar as latas por tamanho
  useEffect(() => {
    const paintCansSize = [18, 3.6, 2.5, 0.5];
    let totalPaint = totalSquare / 5;
    const totalCans = [];

    paintCansSize.forEach((tin, index) => {
      if (tin < totalPaint) {
        totalPaint -= tin;
        totalCans.push(index);
      }
      return null;
    });

    if (totalPaint > 0) {
      totalCans.push(3);
    }

    const total = totalCans.map((size) => paintCansSize[size]);
    setAllCans(total);
  }, [setAllCans, allCans]);

  console.log(totalSquare);
  console.log(allCans);

  return (
    <div className="results-container">
      <div className="results">
        <h1>Olá {name}.</h1>
        <h1>Você precisa de</h1>
        {allCans.map((tin, index) => {
          return <h2 key={ `${tin}-${index}` }>1 lata de {tin}l</h2>
        })}
        <h1>para colorir {totalSquare}m².</h1>
      </div>
    </div>
  );
}

export default ResultPage;
