import React, { useContext, useEffect } from 'react';
import myContext from '../Context/myContext';
import '../CSS/main.css';

function ResultPage() {
  const {
    name,
    heightOne,
    heightTwo,
    heightThree,
    heightFour,
    widthtOne,
    widthtTwo,
    widthtThree,
    widthtFour,
    firstWindow,
    secondWindow,
    thirdWindow,
    fourthWindow,
    firstDoor,
    secondDoor,
    thirdDoor,
    fourthDoor,
  } = useContext(myContext);
  const windowSquare = 24000;
  const doorSquare = 15200;

  useEffect(() => {
    const wallOne = (Number(heightOne) * Number(widthtOne)) / 100;
    const wallTwo = (Number(heightTwo) * Number(widthtTwo)) / 100;
    const wallThree = (Number(heightThree) * Number(widthtThree)) / 100;
    const wallFour = (Number(heightFour) * Number(widthtFour)) / 100;

    const sumAllWindows = (Number(firstWindow) + Number(secondWindow) + Number(thirdWindow) + Number(fourthWindow)) * windowSquare;
    const sumAllDoors = (Number(firstDoor) + Number(secondDoor) + Number(thirdDoor) + Number(fourthDoor)) * doorSquare;
    const total = (wallOne + wallTwo + wallThree + wallFour) - (sumAllWindows + sumAllDoors);
    console.log(wallOne);
    console.log(wallTwo);
    console.log(wallThree);
    console.log(wallFour);
    console.log(total);
  });

  return (
    <div className="page-container">
      <div className="results">
        <h1>Olá {name}.</h1>
        <h1>Você precisa de</h1>
      </div>
    </div>
  )
}

export default ResultPage;
